package test;

public class Four_StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      String name="Shivam";
      String name1="john";
      System.out.println("Num of chars in string: " +name1.length());
      System.out.println("the 4th char in string is:" +name1.charAt(2));
	  System.out.println("the last char in string is:" +name1.charAt(name1.length()-1));
	  System.out.println("The String in upper case is:"+name1.toUpperCase());
	}	
}
